import React, { Component } from 'react';
import {Button} from 'react-bootstrap';
import Modal from 'react-bootstrap/Modal';
import './Munch.css';

class AddPlayerModal extends Component {
    constructor(...args) {
        super(...args);
        this.state = {
            showModal: this.props.showModal,
            title: this.props.title
        };
        this.close = () => {
            this.setState({ showModal: this.props.showModal });
        };

    }
    componentWillReceiveProps(nextProps) {
        this.setState({
            showModal: nextProps.showModal
        })
    }

    changeIt(count)
    {
    var my_div = document.getElementById("my_div")
    var i;
    for(i=0;i<count;i++)
        {
            my_div.innerHTML = my_div.innerHTML +"<br><input type='text' placeholder='Player "+i+"'>";
        }
    }


    // removeAnswer() {
    //     var a_id = sessionStorage.getItem('a_id');
    //     fetch(`http://localhost:4001/Answers/DelA/` + a_id, {
    //         method: 'DELETE',
    //         headers: {
    //             'Content-Type': 'application/json'
    //         }
    //     })
    //         .then(response => {
    //             if (response.status === 200) {
    //                 console.log('Answer Deleted');
    //                 sessionStorage.setItem('ReloadingAfterPost',true)
    //                 window.location.reload();
    //             } else {
    //                 alert('Failed to delete answer');
    //             };
    //         })
    // }

    render() {
        return (
            <Modal
                show={this.state.showModal}
                onHide={this.props.close ? this.props.close : this.close}
                backdrop={true}
            >
                <br/>
                <Modal.Header>
                    <h3 className='modalHead'>Name your Champions!</h3>
                    <br/>
                </Modal.Header>
                <Modal.Body className='modalBod'>
                <text>Amount (Max 4) - </text>
                <input className="inputBoxModal" onChange={this.changeIt}/>
                <div id='my_div'></div>
                </Modal.Body>
                <Modal.Footer className = 'modalFoot'>
                    <Button onClick={this.removeAnswer} className='modalConfirm'> Confirm
                    </Button><br></br>
                    <Button onClick={this.props.close ? this.props.close : this.close} className='modalClose'> Close
                    </Button>
                </Modal.Footer>
            </Modal>
        );
    }
}
export default AddPlayerModal;
