import React, { Component } from 'react';
import {Button} from 'react-bootstrap';
import Modal from 'react-bootstrap/Modal';
import '../css/modalStyle.css';

class AddPlayerModal extends Component {
    constructor(...args) {
        super(...args);
        this.state = {
            showModal: this.props.showModal,
            title: this.props.title
        };
        this.close = () => {
            this.setState({ showModal: this.props.showModal });
        };

    }
    componentWillReceiveProps(nextProps) {
        this.setState({
            showModal: nextProps.showModal
        })
    }

    updatePlayerNumber = (e) => {
        this.setState((prevState) => {
            this.state = {
                ...prevState,
                playerNumber: e
            }
        })
    }
  render() {
      var playerInputs = [];
    for (let i = 0; i<= this.state.playerNumber; i++){
        playerInputs.push(<input key={i} />)
    };
    return (
      <Modal
        show={this.state.showModal}
        onHide={this.props.close ? this.props.close : this.close}
        backdrop={true}
      >
        <br />
        <Modal.Header>
          <h3 className="modalHead">Name your Champions!</h3>
          <br />
        </Modal.Header>
        <Modal.Body className="modalBod">
          <text className="baseText">Amount (Max 4) - </text>
          <input id="playerNumber" className="inputBoxModal" type="number" value={this.state.playerNumber} onChange={this.updatePlayerNumber} />
          <div id="my_div">
          {playerInputs}
          </div>
        </Modal.Body>
        <Modal.Footer className="modalFoot">
          <Button onClick={this.removeAnswer} className="modalConfirm">
            Confirm
          </Button>
          <br></br>
          <Button
            onClick={this.props.close ? this.props.close : this.close}
            className="modalClose"
          >
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    );
  }
}
export default AddPlayerModal;
