import React, { Component } from 'react';
import {Button} from 'react-bootstrap';
import Modal from 'react-bootstrap/Modal';
import './Munch.css';

class AddPlayerModal extends Component {
    constructor(...args) {
        super(...args);
        this.state = {
            showModal: this.props.showModal,
            title: this.props.title
        };
        this.close = () => {
            this.setState({ showModal: this.props.showModal });
        };

    }
    componentWillReceiveProps(nextProps) {
        this.setState({
            showModal: nextProps.showModal
        })
    }

    // removeAnswer() {
    //     var a_id = sessionStorage.getItem('a_id');
    //     fetch(`http://localhost:4001/Answers/DelA/` + a_id, {
    //         method: 'DELETE',
    //         headers: {
    //             'Content-Type': 'application/json'
    //         }
    //     })
    //         .then(response => {
    //             if (response.status === 200) {
    //                 console.log('Answer Deleted');
    //                 sessionStorage.setItem('ReloadingAfterPost',true)
    //                 window.location.reload();
    //             } else {
    //                 alert('Failed to delete answer');
    //             };
    //         })
    // }

    render() {
        return (
            <Modal
                show={this.state.showModal}
                onHide={this.props.close ? this.props.close : this.close}
                backdrop={true}
                backdropClassName="backdrop-style"
                dialogClassName="modal-style"
                aria-labelledby="modal-label"
            >
                <Modal.Header closeButton={true}>
                    <h4 id="modal-label">Amount of Players</h4>
                </Modal.Header>
                <Modal.Body>
                {/* <h5 id="modal-label"></h5> */}
                </Modal.Body>
                <Modal.Footer className = 'modalFoot'>
                    <Button onClick={this.removeAnswer}> Confirm
                    </Button><br></br>
                    <Button onClick={this.props.close ? this.props.close : this.close}> Close
                    </Button>
                </Modal.Footer>
            </Modal>
        );
    }
}
export default AddPlayerModal;
