import React from "react";
import "semantic-ui-css/semantic.min.css";
import { Button, Icon, Input } from "semantic-ui-react";
import "./Munch.css";
import "./frontBackground.jpg";

var placeholder = 'This is a line \nthis should be a new line';
$('textarea').attr('value', placeholder);

$('textarea').focus(function(){
    if($(this).val() === placeholder){
        $(this).attr('value', '');
    }
});

$('textarea').blur(function(){
    if($(this).val() ===''){
        $(this).attr('value', placeholder);
    }    
});

function App() {
  return (
    <div>
      <head>
        <link
          href="https://fonts.googleapis.com/css?family=Almendra SC"
          rel="stylesheet"
        ></link>
      </head>
      <body>
        <h1 class="glowpulsed">Munchkin</h1>
          <placeholder className = "playersButt"></placeholder>
        <div>
          <Button animated className="startButt">
            <Button.Content visible>Start Game</Button.Content>
            <Button.Content hidden>
              <Icon name="bomb" class="icon"/>
            </Button.Content>
          </Button>
          </div>
      </body>
    </div>
  );
}

export default App;
