import React from "react";
import Game from "./MunchRound"
import "semantic-ui-css/semantic.min.css";
import { Button, Icon } from "semantic-ui-react";
import "./Munch.css";
import "./frontBackground.jpg";


export default class Start extends React.Component {

constructor(){
  super();
  this.state = {                                                        //this.state represent the rendered values, i.e. what’s currently on the screen
    gamePage: false
}

}



render() {

  if(this.state.gamePage===true){
    return <div>
               <Game/>
           </div>
  }

  else{
  return (
    <div class = "container">
      <head>
        <link
          href="https://fonts.googleapis.com/css?family=Almendra SC"
          rel="stylesheet"
        ></link>
      </head>
      <body class = "container">
        <h1 class="glowpulsed">Munchkin</h1>
          <div className = "playersButt">
            <input className = "buttInput" placeholder = "Amount of Players" ></input>
          </div>
        <div>
          <Button animated className="startButt">
            <Button.Content visible>Start Game</Button.Content>
            <Button.Content hidden>
              <Icon name="bomb" class="icon"/>
            </Button.Content>
          </Button>
          </div>
          <div className = "levelButt">
            <input className = "buttInput" placeholder = "Level to Win"></input>
          </div>
      </body>
    </div>
  );
}
}
}
